<?php  class  Conexion {	private $servidor = "localhost";	private $usuario = "root";	private $password = "root";	private $bd = "unisangil";	public function conectar(){		$conexion = new mysqli(			$this->servidor,			$this->usuario, 			$this->password,			$this->bd);				if($conexion->connect_errno){			echo "Error al conectar con ".$servidor." ".$conexion->connect_error;		}else{			$conexion->set_charset("utf8");			return $conexion;		}	}} ?>